mapper.insert:
insert into payment (
    payment_number, payment_title, payment_price, payment_method, payment_agency, 
    member_email, lesson_number, payment_date, payment_state, payment_nickname
) values (
    #{payment_number}, #{payment_title}, #{payment_price}, #{payment_method}, 
    #{payment_agency}, #{member_email}, #{lesson_number}, #{payment_date}, #{payment_state}, #{payment_nickname}
)
